local reserve = 8
function get_money()
	if not it(po(16,1)):exists() then
		if reserve > 0 then
			reserve = reserve -1
			wo[po(16,1)] = {"it_coin_m"}
		else
			wo[po(16,1)] = {"it_document", text="text1"}
		end
	end
end
