function s1()
  if st(no["ta"]):is("st_redbrown") then 
    no["whA"].destination = "d1"
  else
    no["whA"].destination = "ta"
  end
end

function s2()
  if st(no["tb"]):is("st_redbrown") then 
    no["whB"].destination = "d2"
  else
    no["whB"].destination = "tb"
  end
end
