function check()
  if po(no["medi%1"]) == po(no["pearl%1"]) and
     po(no["medi%2"]) == po(no["pearl%2"]) and
     po(no["medi%3"]) == po(no["pearl%3"]) and 
     po(no["medi%4"]) == po(no["pearl%4"]) then
    no["chk"]:kill()
  end
end
