el:protected.el:luamain:
wo["ConserveLevel"] = true

ti[" "] = {"fl_wood"}
ti["~"] = {"fl_water"}

if wo["IsDifficult"] then
    ti["#"] = {"st_portal_pearl"} .. ti["~"]
    ti["."] = ti[" "]
    ti["1"] = {"#ac_marble"}
    ti["*"] = {"it_squashed", checkerboard=0} .. ti{"it_burnable_ash", checkerboard=1}
    ti["!"] = {"it_brush"}
else
    ti["#"] = {"st_metal"}
    ti["."] = {"it_blocker"}
    ti["1"] = {"#ac_marble"} .. ti{"it_pin"}
    ti["*"] = {"it_nil"}
    ti["!"] = {"it_nil"}
end

ti["P"] = {"st_plop_slate"}
ti["hole"] = ti["P"] .. {"it_wormhole", destination="dest%%", range=1, strength=5}
ti["dest"] = {"fl_wood", "dest%%"}  .. ti["*"]
ti["|"] = ti["P"] .. {"it_dynamite"}
ti["h"] = {"it_meditation"}
ti["s"] = {"it_spade"}
ti["t"] = {"it_document", text="Health"}

ti["2"] = {"#ac_pearl", owner=DEFAULT, controllers=CTRL_NONE, essential=INDISPENSIBLE}

ti["T"] = ti["~"] .. {"st_timer", invisible=1, interval=0.1, target="wave"}

wo(res.autotile(ti, {"a","d","dest"},{"A","D","hole"}), " ", {
    "T#..*     *     ..#~",
    "~#.. t a    1  *..#~",
    "~#..    s  *  ! C|#~",
    "~#|D         h  ..#~",
    "~#..*   *    b  ..#~",
    "~#..            ..#~",
    "~#.. c  * h  *  ..#~",
    "~#..            ..#~",
    "~#|A       *    ..#~",
    "~#.. h          B|#~",
    "~#..  *   d     ..#~",
    "~#..          * ..#~",
    "~#..*   2222    ..#~"
})

time = 5
dir = 1
function wave()     
    -- the deep sea
    if wo["IsDifficult"] then
        wo["GlobalForce"] = po(dir * 40, 0)
    else
        wo["GlobalForce"] = po(dir * 30, 0)
    end
    time = time + 1;
    if (time == 10) then
        time = 0
        dir = -dir
    end
end
