        "OBS B B BB S  BBB BO",
        "# BBBBBBBBBBBBBBB  #",
        "##O#O###O##O###O#O##"})

wo:shuffleOxyd()

is_day = true
function change_design()
    if is_day then
        wo[no["floor#*"]] = ti["n"]
        wo[no["wall#*"]] = ti["N"]
    else
        wo[no["floor#*"]] = ti[" "]
        wo[no["wall#*"]] = ti["#"]  
    end
    is_day = not is_day
end
