pearl_keys = lib.lua.shuffle({"A","B","C","D","E","F","G","H","I","J"})
function myres(key, x, y)
   local idx = ("ABCDEFGHIJ"):find(key, 1, true)
   if idx ~= nil then
       return ti[pearl_keys[idx]]
   else
       return ti[key]
   end
end

w, h = wo(res.autotile(myres,{"0","9","x"}), " ", {
