wo:shuffleOxyd()

function burn(value, sender)
    burngroup = no["burnme#*"]
    burngroup:setfire()
    maybeburngroup = no["maybeburnme#*"]
    maybeburngroup:shuffle()
    if #maybeburngroup > 30 then
        n = 30
    else
        n = #maybeburngroup
    end
    for j = 1, n do
        fl(maybeburngroup[j]):setfire()
    end
end
